import React from 'react';
type Anchor = React.AnchorHTMLAttributes<HTMLAnchorElement> & {
    anchor?: true;
};
type NoAnchor = Pick<Anchor, 'children'> & {
    anchor?: false;
};
export type DropdownItemProps = Anchor | NoAnchor;
declare const DropdownItem: React.ForwardRefExoticComponent<DropdownItemProps & React.RefAttributes<HTMLAnchorElement>>;
export default DropdownItem;
